sp_widget.client_script:
function($scope, $http) {
  const c = this;

  console.log("ðŸŸ¦ Client script started");

  c.data.rows = [];
  c.data.agentNames = [];
  c.originalRows = [];

  // Fetch data from server
  c.server.get().then(function(response) {
    console.log("ðŸŸ© Server response:", response);

    c.originalRows = response.data.rows || [];
    c.data.rows = [...c.originalRows];

    // âœ… Bind agent names to dropdown
    c.data.agentNames = response.data.agentNames || [];
    console.log("âœ… Agent names received:", c.data.agentNames);
  });

  // Dropdown filter handler
  c.filterByAgent = function() {
    console.log("ðŸ”¹ Filter triggered. Selected agent:", c.selectedAgent);

    if (!c.selectedAgent) {
      console.log("ðŸ”¸ No agent selected, restoring full list");
      c.data.rows = [...c.originalRows];
    } else {
      c.data.rows = c.originalRows.filter(row =>
        row.agent_name === c.selectedAgent
      );
      console.log(`ðŸ”¸ Filtered row count: ${c.data.rows.length}`);
    }
  };
}

