sys_variable_value.value:
(function execute(inputs, outputs) {
    gs.info('[DEBUG] Entered CheckAutomationStatusByInventoryID script step');
    var inventory_id = inputs.inventory_id;
    gs.info('[DEBUG] Input - inventory_id: ' + inventory_id);
    var status = new CheckAutomationStatusByInventoryId().checkStatus(inventory_id);
    gs.info('[DEBUG] Output - status returned from Script Include: ' + status);
    outputs.automation_status = status;
    gs.info('[DEBUG] Output - automation_status set in Flow: ' + outputs.automation_status);
})(inputs, outputs);

