sys_variable_value.value:
(function execute(inputs, outputs) {
    var count = parseInt(inputs.retryCount || 0);
    count += 1;
    retryCount = count; // updates global flow variable
    outputs.retryCount = count; // returns new value
    return outputs;
})(inputs, outputs);

